import React from 'react';
import { useDrop } from 'react-dnd';
import Task from './Task';

const Column = ({ columnId, column, moveTask, addTask, deleteColumn, deleteTask }) => {
    const [{ isOver }, drop] = useDrop({
        accept: 'TASK',
        drop: (item, monitor) => {
            const dropIndex = column.items.length; // Default to the end if not specified

            // Calculate the index based on mouse position
            const clientOffset = monitor.getClientOffset();
            const columnRect = monitor.getTargetClientRect();

            if (clientOffset && columnRect) {
                const dropPosition = clientOffset.y - columnRect.top;
                const dropIndex = column.items.findIndex((_, index) => {
                    const taskRect = document.getElementById(`task-${index}`).getBoundingClientRect();
                    return dropPosition < taskRect.bottom - columnRect.top;
                });
                moveTask(item.id, item.sourceColumnId, columnId, dropIndex < 0 ? column.items.length : dropIndex);
            }
        },
        collect: (monitor) => ({
            isOver: !!monitor.isOver(),
        }),
    });

    return (
        <div
            ref={drop}
            className="column"
            style={{ backgroundColor: isOver ? 'lightgreen' : 'lightgrey' }}
        >
            <h2>{column.name} 
                <span 
                    className="trash-icon" 
                    onClick={() => deleteColumn(columnId)}
                    role="img" 
                    aria-label="trash"
                >
                    üóëÔ∏è
                </span>
                <span 
                    className="add-icon" 
                    onClick={() => addTask(columnId)}
                    role="img" 
                    aria-label="add"
                    style={{ marginLeft: '10px', cursor: 'pointer' }}
                >
                    ‚ûï
                </span>
            </h2>
            {column.items.map((task, index) => (
                <Task key={task.id} task={task} columnId={columnId} index={index} deleteTask={deleteTask} />
            ))}
        </div>
    );
};

export default Column;
