import React from 'react';

const Task = ({ task, columnId, deleteTask }) => {
  return (
    <div className="task">
      {task.content}
      <span 
        className="delete-icon" 
        onClick={() => deleteTask(columnId, task.id)} // Call deleteTask with columnId and task.id
        role="img" 
        aria-label="delete"
        style={{ marginLeft: '10px', cursor: 'pointer' }} // Add margin and cursor style
      >
        ‚ùå
      </span>
    </div>
  );
};

export default Task;
