import React, { useState, useEffect } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import Column from './Column';
import './styles.css';

function App() {
    const [columns, setColumns] = useState(() => {
        const savedColumns = localStorage.getItem('columns');
        return savedColumns ? JSON.parse(savedColumns) : {
            todo: {
                name: 'To Do',
                items: [{ id: '1', content: 'Task 1' }, { id: '2', content: 'Task 2' }],
            },
            ongoing: {
                name: 'Ongoing',
                items: [],
            },
            completed: {
                name: 'Completed',
                items: [],
            },
        };
    });

    // Save columns to local storage when columns state changes
    useEffect(() => {
        localStorage.setItem('columns', JSON.stringify(columns));
    }, [columns]);

    // Function to move task between columns or reorder within the same column
    const moveTask = (taskId, sourceColumnId, destinationColumnId, destinationIndex) => {
        const sourceColumn = columns[sourceColumnId];
        const destColumn = columns[destinationColumnId];

        const taskToMove = sourceColumn.items.find((item) => item.id === taskId);
        const updatedSourceItems = sourceColumn.items.filter((item) => item.id !== taskId);

        if (sourceColumnId === destinationColumnId) {
            // Reordering within the same column
            const reorderedItems = [...updatedSourceItems];
            reorderedItems.splice(destinationIndex, 0, taskToMove);
            setColumns((prevColumns) => ({
                ...prevColumns,
                [sourceColumnId]: {
                    ...sourceColumn,
                    items: reorderedItems,
                },
            }));
        } else {
            // Moving to a different column
            const updatedDestItems = [...destColumn.items, taskToMove];
            setColumns((prevColumns) => ({
                ...prevColumns,
                [sourceColumnId]: {
                    ...sourceColumn,
                    items: updatedSourceItems,
                },
                [destinationColumnId]: {
                    ...destColumn,
                    items: updatedDestItems,
                },
            }));
        }
    };

    // Function to add a new task to a column
    const addTask = (columnId) => {
        const taskContent = prompt("Enter task content:");
        if (taskContent) {
            const newTask = { id: Date.now().toString(), content: taskContent };
            setColumns((prevColumns) => {
                const updatedColumn = {
                    ...prevColumns[columnId],
                    items: [...prevColumns[columnId].items, newTask],
                };
                return { ...prevColumns, [columnId]: updatedColumn };
            });
        }
    };

    // Function to add a new column
    const addColumn = () => {
        const columnName = prompt("Enter new column name:");
        if (columnName) {
            const newColumnId = `column_${Date.now()}`;
            setColumns((prevColumns) => ({
                ...prevColumns,
                [newColumnId]: { name: columnName, items: [] },
            }));
        }
    };

    // Function to delete a column
    const deleteColumn = (columnId) => {
        setColumns((prevColumns) => {
            const newColumns = { ...prevColumns };
            delete newColumns[columnId];
            return newColumns;
        });
    };

    // New function to delete a specific task
    const deleteTask = (columnId, taskId) => {
        setColumns((prevColumns) => {
            const updatedColumn = {
                ...prevColumns[columnId],
                items: prevColumns[columnId].items.filter((task) => task.id !== taskId),
            };
            return { ...prevColumns, [columnId]: updatedColumn };
        });
    };

    return (
        <DndProvider backend={HTML5Backend}>
            <div className="App">
                <h1>Pixy Board</h1>
                <button onClick={addColumn}>Add Column</button>

                <div className="kanban-board-wrapper">
                    <div className="kanban-board">
                        {Object.entries(columns).map(([columnId, column]) => (
                            <Column 
                                key={columnId} 
                                columnId={columnId} 
                                column={column} 
                                moveTask={moveTask} 
                                addTask={addTask} 
                                deleteColumn={deleteColumn} 
                                deleteTask={deleteTask} 
                            />
                        ))}
                    </div>
                </div>
            </div>
        </DndProvider>
    );
}

export default App;
