import React from 'react';
import { useDrop } from 'react-dnd';
import Task from './Task';

const Column = ({ columnId, column, moveTask, addTask, deleteColumn }) => {
  const [{ isOver }, drop] = useDrop({
    accept: 'TASK',
    drop: (item) => {
      const destinationIndex = column.items.length; // Default to the end if not specified
      moveTask(item.id, item.sourceColumnId, columnId, destinationIndex);
    },
    collect: (monitor) => ({
      isOver: !!monitor.isOver(),
    }),
  });

  return (
    <div
      ref={drop}
      className="column"
      style={{ backgroundColor: isOver ? 'lightgreen' : 'lightgrey' }}
    >
      <h2>{column.name} 
        <span 
          className="trash-icon" 
          onClick={() => deleteColumn(columnId)}
          role="img" 
          aria-label="trash"
        >
          üóëÔ∏è
        </span>
        <span 
          className="add-icon" 
          onClick={() => addTask(columnId)}
          role="img" 
          aria-label="add"
          style={{ marginLeft: '10px', cursor: 'pointer' }} // Add margin and cursor style
        >
          ‚ûï
        </span>
      </h2>
      {column.items.map((task, index) => (
        <Task key={task.id} task={task} columnId={columnId} index={index} />
      ))}
    </div>
  );
};

export default Column;
