import React, { useState } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import Column from './Column';
import './styles.css';

function App() {
  const [columns, setColumns] = useState({
    todo: {
      name: 'To Do',
      items: [{ id: '1', content: 'Task 1' }, { id: '2', content: 'Task 2' }],
    },
    ongoing: {
      name: 'Ongoing',
      items: [],
    },
    completed: {
      name: 'Completed',
      items: [],
    },
  });

  const moveTask = (taskId, sourceColumnId, destinationColumnId, destinationIndex) => {
    const sourceColumn = columns[sourceColumnId];
    const destColumn = columns[destinationColumnId];

    const taskToMove = sourceColumn.items.find((item) => item.id === taskId);
    const updatedSourceItems = sourceColumn.items.filter((item) => item.id !== taskId);

    if (sourceColumnId === destinationColumnId) {
      // If moving within the same column, we just reorder
      const reorderedItems = [...updatedSourceItems];
      reorderedItems.splice(destinationIndex, 0, taskToMove);
      setColumns({
        ...columns,
        [sourceColumnId]: {
          ...sourceColumn,
          items: reorderedItems,
        },
      });
    } else {
      // If moving to a different column, we need to add to destination
      const updatedDestItems = [...destColumn.items, taskToMove];
      setColumns({
        ...columns,
        [sourceColumnId]: {
          ...sourceColumn,
          items: updatedSourceItems,
        },
        [destinationColumnId]: {
          ...destColumn,
          items: updatedDestItems,
        },
      });
    }
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="App">
        <h1>Kanban Board</h1>
        <div className="kanban-board">
          {Object.entries(columns).map(([columnId, column]) => (
            <Column
              key={columnId}
              columnId={columnId}
              column={column}
              moveTask={moveTask}
            />
          ))}
        </div>
      </div>
    </DndProvider>
  );
}

export default App;
