import React from 'react';
import { useDrop } from 'react-dnd';
import Task from './Task';

const Column = ({ columnId, column, moveTask }) => {
    const [{ isOver }, drop] = useDrop({
        accept: 'TASK',
        drop: (item, monitor) => {
          const dropResult = monitor.getDropResult();
          const destinationIndex = dropResult.index; // Get the destination index
          moveTask(item.id, item.sourceColumnId, columnId, destinationIndex);
        },
        collect: (monitor) => ({
          isOver: !!monitor.isOver(),
        }),
      });
      

  return (
    <div
      ref={drop}
      className="column"
      style={{ backgroundColor: isOver ? 'lightgreen' : 'lightgrey' }}
    >
      <h2>{column.name}</h2>
      {column.items.map((task) => (
        <Task key={task.id} task={task} columnId={columnId} />
      ))}
    </div>
  );
};

export default Column;
