import React from 'react';
import { useDrop } from 'react-dnd';
import Task from './Task';

const Column = ({ columnId, column, moveTask, addTask }) => {
  const [{ isOver }, drop] = useDrop({
    accept: 'TASK',
    drop: (item, monitor) => {
      const destinationIndex = monitor.getDropResult().index; // Get the destination index
      moveTask(item.id, item.sourceColumnId, columnId, destinationIndex);
    },
    collect: (monitor) => ({
      isOver: !!monitor.isOver(),
    }),
  });

  return (
    <div
      ref={drop}
      className="column"
      style={{ backgroundColor: isOver ? 'lightgreen' : 'lightgrey' }}
    >
      <h2>{column.name}</h2>
      <button onClick={() => addTask(columnId)}>Add Task</button>
      {column.items.map((task, index) => (
        <Task key={task.id} task={task} columnId={columnId} index={index} />
      ))}
    </div>
  );
};

export default Column;
